  }

  .nav__links-aside {
    grid-auto-flow: row;
  }

  &__linkBox {
    position: relative;
    z-index: 1;
  }

  &__icon {
    position: absolute;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    margin-left: 2rem;
    z-index: 3;
    fill: var(--clr-w);
  }

  &__link {
    width: 100%;
    height: 100%;
    display: grid;
    place-items: center;
    overflow: hidden;

    position: relative;
    z-index: 2;

    @include mq(d) {
      justify-items: start;
      padding-left: 7rem;
    }

    @include mq(d-big) {
      padding-left: 11rem;
    }

    &:link,
    &:visited {
      color: var(--clr-w);
      text-transform: uppercase;
      letter-spacing: 0.1rem;
    }

    &::after {
      content: '';
      position: absolute;
      z-index: -1;
      top: 100%;
      left: 0;
      width: 5px;
      height: 100%;
      background-color: var(--clr-prim);
    }

    &:hover::after {
      animation: navLinkHover 0.5s cubic-bezier(1, 0, 0.16, 1) forwards;
    }
  }

  &__active {
    background-color: var(--clr-prim);
  }
}

