.btn {
  &-inline {
    border: none;
    color: var(--clr-prim);
    font-size: inherit;
    font-family: inherit;
    background-color: transparent;
    border-bottom: 1px solid var(--clr-prim);
    padding-bottom: 2px;
    display: inline-block;
    cursor: pointer;
    justify-self: center;
    transition: all 0.3s ease;

    &:hover {
      color: var(--color-grey-dark-1);
      border-bottom-color: var(--color-grey-dark-1);
    }
  }

  &__arr {
    transition: all 0.3s ease;
  }

  &-inline:hover &__arr {
    margin-left: 5px;
  }

  &__cta {
    font-size: 1.5rem;
    font-weight: 300;
    text-transform: uppercase;
    border-radius: 100px;
    border: none;
    color: #fff;
    padding: 2rem 10rem;
    max-width: 50px;
    background-image: linear-gradient(
      to right,
      var(--clr-prim-light),
      var(--clr-prim-dark)
    );
    position: relative;
    overflow: hidden;
    cursor: pointer;

    &::after {
      content: 'Only 4 rooms left';
      position: absolute;
      top: -100%;
      left: 0;
      width: 100%;
      height: 100%;
      text-transform: uppercase;
      transition: all 0.3s ease;

      display: grid;
      align-items: center;
    }

    &::before {
      content: 'Book now';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      transition: all 0.3s ease;

      display: grid;
      align-items: center;
    }

    &:hover {
      background-image: linear-gradient(
        to right,
        var(--clr-prim-dark),
        var(--clr-prim-light)
      );
    }

    &:hover::after {
      transform: translateY(100%);
    }

    &:hover::before {
      transform: translateY(100%);
    }
  }
}

